void BACKGROUND::Scroll(int s)
{
	// pure visual rng
	// twister_switch(1);

	for (int i=0; i<s; i++)
	{
		int q = twister_rand()%(3*h);

		int a = scroll%w;

		if (color)
		{
			for (int y=0; y<h; y++)
			{
				buf[(w+1)*y+a] = '.';
				color[(w+1)*y+a] = tcolor;
			}

			if (q<h)
			{
				buf[(w+1)*q+a] = '.';
				color[(w+1)*q+a] = (cl_transp&0xF0)+9+twister_rand()%7;
			}
		}
		else
		{
			for (int y=0; y<h; y++)
				buf[(w+1)*y+a] = ' ';

			if (q<h)
			{
				buf[(w+1)*q+a] = '.';
				twister_rand(); // keep in sync with color
			}
		}

		scroll++;
	}

	//twister_switch(0);
}

